import React, { useState } from 'react';
import SlideEditor from './component/SlideEditor';
import './styles/main.scss';

const App = () => {
  const [slides, setSlides] = useState([]);

  const handleRefresh = () => {
    const confirmed = showConfirmationDialog('새로고침 하시겠습니까? 이전에 작업한 내용은 초기화됩니다.');
    if (confirmed) {
      localStorage.removeItem('slides');
      window.location.reload();
    }
  };

  const showConfirmationDialog = (message) => {
    return new Promise((resolve) => {
      const modal = document.createElement('div');
      modal.className = 'modal';
      modal.innerHTML = `
        <div class="modal-content">
          <p>${message}</p>
          <button id="confirmBtn">확인</button>
          <button id="cancelBtn">취소</button>
        </div>
      `;

      const confirmBtn = modal.querySelector('#confirmBtn');
      const cancelBtn = modal.querySelector('#cancelBtn');

      const handleConfirm = () => {
        document.body.removeChild(modal);
        resolve(true);
      };

      const handleCancel = () => {
        document.body.removeChild(modal);
        resolve(false);
      };

      confirmBtn.addEventListener('click', handleConfirm);
      cancelBtn.addEventListener('click', handleCancel);

      document.body.appendChild(modal);
    });
  };

  return (
    <div>
      <h1>Sample ver01.00.00</h1>
      <SlideEditor initialSlides={slides} setSlides={setSlides} />
      <button onClick={handleRefresh}>새로고침</button>
    </div>
  );
};

export default App;
