import React, { useState } from 'react';
import ImageUploader from './ImageUploader';

import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination, Scrollbar, A11y } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const SlideList = ({ slides, onAddSlide, onDeleteSlide, onImageUpload }) => {
  
  const [outputMarkup, setOutputMarkup] = useState('');
  const [outputStyle, setOutputStyle] = useState('');
  const [outputScript, setOutputScript] = useState('');

  const handleComplete = () => {
    const { markup, style, script } = generateCode(slides);
    setOutputMarkup(markup);
    setOutputStyle(style);
    setOutputScript(script);
  };

  
  const handleOutput = () => {
    const outputWindow = window.open();
    outputWindow.document.write(
      `<html>
        <head>
          <style>${outputStyle}</style>
        </head>
        <body>
          ${outputMarkup}
          <script>${outputScript}</script>
        </body>
      </html>`
    );
    outputWindow.document.close();
  };

  const generateCode = (slides) => {
    // 슬라이드 정보를 기반으로 마크업, 스타일, 스크립트 정보를 생성하는 로직을 작성하세요
    // 예시로는 슬라이드 제목을 순서대로 나열하는 마크업, 스타일, 스크립트 정보를 생성합니다
    const markup = slides.map((slide, index) => `<h2>${slide.title}</h2>`).join('');
    const style = `.slide { /* 슬라이드 스타일 */ }`;
    const script = `// 슬라이드 관련 스크립트`;
    return { markup, style, script };
  };


  return (
    <div>
      <Swiper
      modules={[Navigation, Pagination, Scrollbar, A11y]}
      spaceBetween={50}
      slidesPerView={1}
      navigation
      pagination={{ clickable: true }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log('slide change')}
      >
      {slides.map((slide, index) => (
        <div key={index}>
           <SwiperSlide>
          <ImageUploader onImageUpload={(imageURL) => onImageUpload(imageURL, index)} />
          <button className='d_delet_slide' onClick={() => onDeleteSlide(index)}>Delete</button>
          </SwiperSlide>
        </div>
      ))}   
      </Swiper>
      <button className='d_add_slide' onClick={onAddSlide}>+</button>
      {/* 완료 버튼 */}
      <button onClick={handleComplete}>완료</button>
      {/* 출력 버튼 */}
      <button onClick={handleOutput}>출력</button>
    </div>
  );
};

export default SlideList;
